import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import SearchBar from './SearchBar';
import SignInForm from '../login/SignInForm';
import LogInForm from '../login/LoginForm';

const pages = ['Home', 'Add Product'];

function NavBar() {

    const [login, setLoggedIn] = React.useState(false);
    const [signUp, setSignUp] = React.useState(false);

    const onClickOfLogIn = () => {
        setSignUp(false);
        setLoggedIn(!login);
        console.log(login)
    }

    const onClickOfSignIn = () => {
        setLoggedIn(false);
        setSignUp(!signUp)
        console.log(signUp)
    }

  return (
    <>
    <AppBar position="static">
      <Container maxWidth="xl">
        <Toolbar disableGutters>
          <ShoppingCartIcon sx={{ display: { xs: 'none', md: 'flex' }, mr: 1 }} />
          <Typography
            variant="h6"
            noWrap
            component="a"
            href="/"
            sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
            }}
          >
            upGrad Eshop
          </Typography>
          <SearchBar/>
          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
            {pages.map((page) => (
              <Button
                key={page}
                sx={{ my: 2, color: 'white', display: 'block' }}
              >
                {page}
              </Button>
            ))}
          </Box>
          <Button color="inherit" onClick={onClickOfLogIn}>Login</Button>
          <Button color="inherit" onClick={onClickOfSignIn}>Sign Up</Button>
        </Toolbar>
      </Container>
    </AppBar>
    <div>
    {login ? <LogInForm/> : signUp ? <SignInForm/> : <p>Please login/Sign In</p>}
    </div>
    </>
  );
}
export default NavBar;
