import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import SearchBar from './SearchBar';
import LoginForm from '../login/LoginForm';

const pages = ['Home', 'Add Product'];

function NavBar() {

    const [login, setLoggedIn] = React.useState(false);

    const renderLoginForm = () => {
        setLoggedIn(true);
        let contentToRender = login ? <LoginForm/> : null
        return ({contentToRender})
    }

    const renderSignUpForm = () => {
        setLoggedIn(true);
        let contentToRender = login ? <LoginForm/> : null
        return ({contentToRender})
    }

  return (
    <>
    <AppBar position="static">
      <Container maxWidth="xl">
        <Toolbar disableGutters>
          <ShoppingCartIcon sx={{ display: { xs: 'none', md: 'flex' }, mr: 1 }} />
          <Typography
            variant="h6"
            noWrap
            component="a"
            href="/"
            sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
            }}
          >
            upGrad Eshop
          </Typography>
          <SearchBar/>
          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
            {pages.map((page) => (
              <Button
                key={page}
                sx={{ my: 2, color: 'white', display: 'block' }}
              >
                {page}
              </Button>
            ))}
          </Box>
          <Button color="inherit" onClick={renderLoginForm}>Login</Button>
          <Button color="inherit" onClick={renderSignUpForm}>Sign Up</Button>
        </Toolbar>
      </Container>
    </AppBar>
    </>
  );
}
export default NavBar;
